$sigPath = "$env:USERPROFILE\AppData\Roaming\Microsoft\Signatures"; $odFolder = (Get-ChildItem -Path "$env:USERPROFILE" -Filter "OneDrive - *" -Directory).FullName; $odPath = if ($odFolder) { Join-Path $odFolder "Documents\OutlookSignatures" }; if ($odPath -and (Test-Path $odPath)) { if (-not (Test-Path $sigPath)) { New-Item -ItemType Directory -Force -Path $sigPath | Out-Null }; Copy-Item -Path "$odPath\*" -Destination $sigPath -Recurse -Force }